CREATE TABLE nga_table_index (
  table_name TEXT NOT NULL PRIMARY KEY,
  last_indexed DATETIME,
  CONSTRAINT fk_nti_ge_tn FOREIGN KEY (table_name) REFERENCES gpkg_extensions(table_name)
);

